package writwjava;

public class OperadorAtribuicao {

  public static void main(String[] args) {
    // TODO Auto-generated method stub
    // Operador de atribuição =
    int x = 10;
    x = x + 15;
    System.out.println(x);

    int y = 10;
    y += 15;
    // y = y + 15
    System.out.println(y);


    int z = 10;
    z *= 15;
    // z = 15 * 10
    System.out.println(z);

  }

}
