package writwjava;

public class VariaveisTipos {

  public static void main(String[] args) {
    // TODO Auto-generated method stub
    int idade = 10;
    double preco = 10.5;
    char sexo = 'M';
    boolean temFilhos = true;

    System.out.println("Tipo Integer : " + idade);
    System.out.println("Tipo Double :" + preco);
    System.out.println("Tipo Char :" + sexo);
    System.out.println("Tipo  Bollean : " + temFilhos);

  }

}
